                data-section-type="footer-brands">
                <h5 class="footer-info-heading">
                    {{lang 'footer.brands'}}
                    <i class="icon" aria-hidden="true">
                        <svg>
                            <use xlink:href="#icon-downselector" />
                        </svg>
                    </i>
