            {{#if settings.address}}
            <article class="footer-info-col footer-mobile-limited footer-info-col--small" data-section-type="storeInfo">
                <!-- <h5 class="footer-info-heading footer-store-name">{{settings.store_name}}</h5> -->
                <div class="footer-info-heading footer-store-name"><a href="/"><img src="{{ cdn '../img/sisu/theme/global/sisu-mouthguards-logo.svg' }}" alt="SISU Mouthguards Logo" /></a></div>
                <!-- <address>Akervall Technologies Inc.</br>{{nl2br settings.address}}</address> -->
                <p>(see'-soo) <em>noun</em> - bravery, tenacity, <strong>grit</strong></p>
                {{#if settings.phone_number}}
                <div class="footer-info-phone">
                    <i class="icon" aria-hidden="true">
                        <svg>
                            <use xlink:href="#icon-phone" />
                        </svg>
                    </i>
                    <a href="tel:{{settings.phone_number}}">{{settings.phone_number}}</a>
                </div>
                {{/if}}
            </article>
            {{/if}}
        </section>
        {{#if theme_settings.geotrust_ssl_common_name}}
        <div class="footer-geotrust-ssl-seal">
            {{> components/common/geotrust-ssl-seal}}
        </div>
        {{/if}}

        <div class="footer-copyright">
            {{> components/common/payment-icons}}
            {{#if theme_settings.show_copyright_footer}}
            <p class="powered-by text-center">&copy; {{moment format="YYYY"}} <a href="http://www.akervalltechnologies.com/">AKERVALL TECHNOLOGIES INC</a> | ALL RIGHTS RESERVED | <a href="/privacy/">PRIVACY POLICY</a> | <a href="/patents/">PATENTS</a></p>
            {{/if}}
        </div>
    </div>
</footer>

